mapper.select:
select t.id,t.name, c.name as customerId, u.name as owner, d1.value as stage, d2.value as type,
       d3.value as source
from tbl_tran t
join tbl_user u on t.owner = u.id
join tbl_customer c on t.customer_id = c.id
join tbl_dic_value d1 on t.stage = d1.id
join tbl_dic_value d2 on t.type = d2.id
join tbl_dic_value d3 on t.source = d3.id

  
    and t.owner like '%' {#owner} '%'
  
  
    and t.name like '%' #{name} '%'
  
  
    and t.customer_id = #{customerId}
  
  
    and t.stage = #{stage}
  
  
    and t.type = #{type}
  
  
    and t.source = #{source}
  

  order by t.create_time desc
  limit #{beginNo}, #{pageSize}

select count(*)
from tbl_tran t
join tbl_user u on t.owner = u.id
join tbl_customer c on t.customer_id = c.id
join tbl_dic_value d1 on t.stage = d1.id
join tbl_dic_value d2 on t.type = d2.id
join tbl_dic_value d3 on t.source = d3.id

  
    and t.owner like '%' {owner} '%'
  
  
    and t.name like '%' #{name} '%'
  
  
    and t.customer_id = #{customerId}
  
  
    and t.stage = #{stage}
  
  
    and t.type = #{type}
  
  
    and t.source = #{source}
  

order by t.create_time desc
limit #{beginNo}, #{pageSize}
